'use client'

import { OptimizedImage } from './optimized-image'
import { cn } from '@/lib/utils'

interface ProductImageOptimizedProps {
  src: string
  alt: string
  className?: string
  priority?: boolean
  sizes?: string
  quality?: number
  onLoad?: () => void
  onError?: () => void
}

export function ProductImageOptimized({
  src,
  alt,
  className,
  priority = false,
  sizes = "(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw",
  quality = 80,
  onLoad,
  onError,
}: ProductImageOptimizedProps) {
  return (
    <OptimizedImage
      src={src}
      alt={alt}
      className={cn("object-cover transition-opacity duration-300", className)}
      priority={priority}
      sizes={sizes}
      quality={quality}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      onLoad={onLoad}
      onError={onError}
    />
  )
}

